  def calc_savings
    monthly_data = data_for_zipcode(params[:zipcode].to_i)
    @monthly_data_json = { data: monthly_data }.to_json.html_safe if monthly_data

    render layout: "landing_page/landing_layout"
  end

  private

  def data_for_zipcode(zipcode)
    case zipcode
      when 94621
        return OAKLAND
      when 92673
        return SAN_CLEMENTE
      when 92101
        return SAN_DIEGO
      else
        flash.now["danger"] =  "You gave me #{zipcode} -- I have no idea what to do with that."
        nil
    end
  end
